mapper.insert:
INSERT INTO tb_board_management
(board_num    AS boardNum
, b_cate_code  AS boardCateCode
, board_title  AS boardTitle
, board_contents  AS boardContents
, board_file_attach  AS boardFile
, board_secret  AS boardSecret
, board_time    AS boardRegTime
, board_views   AS boardViews
, member_id     AS memberId
, state_code    AS stateCode
)
VALUES (
  #{boardNum}
, #{boardCateCode}
, #{boardTitle}
, #{boardContents}
, #{boardFile}
, #{boardSecret}
, NOW()
, #{boardViews}
, #{memberId}
, #{stateCode});
