mapper.update:
UPDATE tb_event
SET event_name = #{eventName}
    , img_original_name = #{eventImgName}
    , banner_original_name = #{eventBannerName}
    , start_time = #{eventStr}
    , end_time = #{eventFin}
    , detail = #{eventDetail}
    , stored_image_path = #{imgStoredPath}
    , stored_banner_path = #{bannerStoredPath}
WHERE event_code = #{eventCode}

mapper.insert:
INSERT INTO tb_event
	(event_code
	, event_name
	, img_original_name
	, banner_original_name
	, start_time
	, end_time
	, detail
	, state_code
	, reg_time
	, admin_id
	, stored_image_path
	, stored_banner_path
	, store_code
	, store_name)
VALUES (
	sf_new_event_code()
	, #{eventName}
	, #{eventImgName}
	, #{eventBannerName}
	, #{eventStr}
	, #{eventFin}
	, #{eventDetail}
	, #{stateCode}
	, NOW()
	, #{adminId}
	, #{imgStoredPath}
	, #{bannerStoredPath}

		, #{storeCode}
		, #{storeName})	


		, null
		, null)

mapper.select:
SELECT e.event_code , e.event_name , e.img_original_name , e.banner_original_name , e.start_time , e.end_time , e.detail , e.state_code , e.reg_time , e.admin_id , e.stored_image_path , e.stored_banner_path , t.state_name , e.store_code , s.store_name
FROM tb_event AS e LEFT JOIN tb_statement AS t ON e.state_code = t.state_code LEFT JOIN tb_store_info as s ON e.store_code = s.store_code
state_code = #{stateCode}
event_code = #{eventCode}
ORDER BY e.reg_time DESC
